void ST::test_present_members() {
  // Make sure the struct picks up ompx_hold even if another element of the
  // struct doesn't have ompx_hold.
  #pragma omp target map(tofrom : i) map(ompx_hold, tofrom : j)
  {
#ifdef USE
    i++;
    j++;
#endif
  }
}

