               aesdec256kl  291(%edi,%eax,4), %xmm2

// CHECK:      aesdec256kl  (%eax), %xmm2
// CHECK: encoding: [0xf3,0x0f,0x38,0xdf,0x10]
               aesdec256kl  (%eax), %xmm2

// CHECK:      aesdec256kl  -2048(,%ebp,2), %xmm2
